//     setUser();
//   } catch (error) {
//    console.log("Something is Wrong");
//   }
//  }
//  console.log(onFormSubmit);

function Json() {
  const [fname, setFname] = useState("");
  const [lname, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [message, setMessage] = useState("");

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://localhost/PHPReact/add_user.php", {
        method: "POST",
        headers:{

          'Accept':'application/json'
      },
        body: JSON.stringify({
          name: fname,
          email: email,
          mobileNumber: mobileNumber,
        }),
      });
      let resJson = await res.json();
      if (res.status === 200) {
        setFname("");
        setName("");
        setEmail("");
        setMobileNumber("");
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };

   return(
<div className="container">
      <form onSubmit={onFormSubmit()}>
        <div className="left">
          <div className="form-group col-md-6">
            <label>First Name</label>
            <input type="text" name="fname" className="form-control" id="fname" 
            onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>Last Name</label>
            <input type="text" name="lname" className="form-control" id="lname"  
            onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>Email</label>
            <input type="text" name="email" className="form-control" id="email"  
            onChange={e => onTextChanges(e)} />
          </div>

          <div className="form-group col-md-6">
            <label>Phone Number</label>
            <input type="text" name="phone" className="form-control" id="phone"  
            onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>Gender</label>
            <input type="text" name="gender" className="form-control" id="gender"  onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>User Role</label>
            <input type="text" name="role" className="form-control" id="role"  onChange={e => onTextChanges(e)} />
          </div>
        </div>
        <div className="right">
          <div className="form-group col-md-6">
            <label>City</label>
            <input type="text" name="city" className="form-control" id="city"  onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>State</label>
            <input type="text" name="state" className="form-control" id="state"  onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>ID Number</label>
            <input type="text" name="id_no" className="form-control" id=""  onChange={e => onTextChanges(e)} />
          </div>
          <div className="form-group col-md-6">
            <label>Address</label>
            <input type="text" name="address" className="form-control" id="address"  onChange={e => onTextChanges(e)} />
          </div>
        </div>
        <button type="submit" className="btn btn-primary" 
        onClick = { e => onFormSubmit(e)} name="submit" >Sign Up</button>
      </form>
    </div>
);
}

export default AddUser;
